function calcularTotal(qtdJantinhas, espetos, refris, lanches = {}, acai = {}, acaiAdicionais = {}) {
    let total = qtdJantinhas * PRECOS.jantinha;

    // espetos
    for (const [nome, qtd] of Object.entries(espetos)) {
        total += qtd * (PRECOS.espetos[nome] || 0);
    }

    // refris
    for (const [nome, qtd] of Object.entries(refris)) {
        total += qtd * (PRECOS.refrigerantes[nome] || 0);
    }

    // lanches
    for (const [nome, qtd] of Object.entries(lanches)) {
        total += qtd * (PRECOS.lanches[nome] || 0);
    }

    // 🆕 AÇAÍ
    for (const [nome, qtd] of Object.entries(acai)) {
        total += qtd * (PRECOS.acai[nome] || 0);
    }
    for (const [nome, qtd] of Object.entries(acaiAdicionais)) {
        total += qtd * (PRECOS.acaiAdicionais[nome] || 0);
    }

    return total;
}

// NOVO: Cálculo de lucros separados (jantinhas, espetos, refrigerantes)
function calcularFinanceiroPedido(pedido) {
    let lucro = {
        jantinhas: 0,
        espetos: 0,
        refrigerantes: 0,
        lanches: 0,
        acai: 0
    };

    let custoTotal = 0;
    let faturamentoTotal = 0;

    // JANTINHAS
    const qJ = pedido.jantinhas?.quantidade || 0;
    if (qJ > 0) {
        const fat = qJ * PRECOS.jantinha;
        const custo = qJ * CUSTOS.jantinha;
        lucro.jantinhas += fat - custo;
        faturamentoTotal += fat;
        custoTotal += custo;
    }

    // ESPETOS
    for (const [nome, qtd] of Object.entries(pedido.espetos || {})) {
        if (qtd > 0) {
            const fat = qtd * (PRECOS.espetos[nome] || 0);
            const custo = qtd * (CUSTOS.espetos[nome] ?? CUSTOS.espetos.default ?? 0);
            lucro.espetos += fat - custo;
            faturamentoTotal += fat;
            custoTotal += custo;
        }
    }

    // REFRIGERANTES
    for (const [nome, qtd] of Object.entries(pedido.refrigerantes || {})) {
        if (qtd > 0) {
            const fat = qtd * (PRECOS.refrigerantes[nome] || 0);
            const custo = qtd * (CUSTOS.refrigerantes[nome] ?? 0);
            lucro.refrigerantes += fat - custo;
            faturamentoTotal += fat;
            custoTotal += custo;
        }
    }

    // LANCHES
    for (const [nome, qtd] of Object.entries(pedido.lanches || {})) {
        if (qtd > 0) {
            const fat = qtd * (PRECOS.lanches[nome] || 0);
            const custo = qtd * (CUSTOS.lanches[nome] ?? 0);
            lucro.lanches += fat - custo;
            faturamentoTotal += fat;
            custoTotal += custo;
        }
    }

    // AÇAÍ + ADICIONAIS
    for (const [nome, qtd] of Object.entries(pedido.acai || {})) {
        const fat = qtd * (PRECOS.acai[nome] || 0);
        const custo = qtd * (CUSTOS.acai[nome] || 0);
        lucro.acai += fat - custo;
        faturamentoTotal += fat;
        custoTotal += custo;
    }

    for (const [nome, qtd] of Object.entries(pedido.acaiAdicionais || {})) {
        const fat = qtd * (PRECOS.acaiAdicionais[nome] || 0);
        const custo = qtd * (CUSTOS.acaiAdicionais[nome] || 0);
        lucro.acai += fat - custo;
        faturamentoTotal += fat;
        custoTotal += custo;
    }

    return {
        faturamentoTotal: +faturamentoTotal.toFixed(2),
        custoTotal: +custoTotal.toFixed(2),
        lucroPorCategoria: {
            jantinhas: +lucro.jantinhas.toFixed(2),
            espetos: +lucro.espetos.toFixed(2),
            refrigerantes: +lucro.refrigerantes.toFixed(2),
            lanches: +lucro.lanches.toFixed(2),
            acai: +lucro.acai.toFixed(2)
        },
        lucroTotal: +(faturamentoTotal - custoTotal).toFixed(2)
    };
}



function calcularJantinhasDoDia(dataAlvo = new Date()) {
    const dia = new Date(dataAlvo).toLocaleDateString();
    let total = 0;

    pedidosSalvos.forEach(pedido => {
        if (!pedido.data) return;

        const dataPedido = new Date(pedido.data).toLocaleDateString();
        if (dataPedido === dia) {
            total += pedido.jantinhas?.quantidade || 0;
        }
    });

    return total;
}



