function analisarTiposDePedido() {
    let contagem = {
        apenasEspetos: 0,
        jantinhaMaisEspetos: 0,
        totalPedidos: pedidosSalvos.length
    };
    function calcularJantinhasHoje() {
        const hoje = new Date().toLocaleDateString();

        let totalJantinhasHoje = 0;

        pedidosSalvos.forEach(pedido => {
            if (!pedido.data) return;

            const dataPedido = new Date(pedido.data).toLocaleDateString();

            if (dataPedido === hoje) {
                totalJantinhasHoje += pedido.jantinhas?.quantidade || 0;
            }
        });

        return totalJantinhasHoje;
    }

    pedidosSalvos.forEach(pedido => {
        const temJantinha = pedido.jantinhas.quantidade > 0;
        const temEspetosAdicionais = Object.values(pedido.espetos).some(qtd => qtd > 0);

        if (temJantinha) {
            contagem.jantinhaMaisEspetos++;
        } else if (temEspetosAdicionais) {
            contagem.apenasEspetos++;
        }
    });

    return contagem;
}

// Adicione ", lanches = {}" nos parênteses abaixo
function verificarEstoque(espetos, refris, lanches = {}, acai = {}) {
    let faltantes = [];

    // Espetos
    for (const [nome, qtd] of Object.entries(espetos)) {
        if (qtd > (ESTOQUE.espetos[nome] || 0)) {
            faltantes.push(`${nome} (Disponível: ${ESTOQUE.espetos[nome] || 0})`);
        }
    }

    // Açaí
    for (const [nome, qtd] of Object.entries(acai)) {
        const estoqueAtual = ESTOQUE.acai[nome];
        if (estoqueAtual !== undefined && qtd > estoqueAtual) {
            faltantes.push(`${nome} (Disponível: ${estoqueAtual})`);
        }
    }


    // Refrigerantes
    for (const [nome, qtd] of Object.entries(refris)) {
        if (qtd > (ESTOQUE.refrigerantes[nome] || 0)) {
            faltantes.push(`${nome} (Disponível: ${ESTOQUE.refrigerantes[nome] || 0})`);
        }
    }

    for (const [nomeLanche, qtdLanche] of Object.entries(lanches)) {
        if (qtdLanche <= 0) continue;

        const ficha = FICHA_TECNICA[nomeLanche];

        if (!ficha) {
            faltantes.push(`${nomeLanche} (Sem ficha técnica)`);
            continue;
        }

        for (const [insumo, qtdPorLanche] of Object.entries(ficha)) {
            const necessario = qtdPorLanche * qtdLanche;
            const disponivel = ESTOQUE.insumos[insumo] || 0;

            if (necessario > disponivel) {
                faltantes.push(
                    `${nomeLanche} → ${insumo} (Precisa ${necessario}, disponível ${disponivel})`
                );
            }
        }
    }
    return faltantes;
}



function atualizarEstoqueECaixa(pedido) {
    // 1. Debita do Estoque
    for (const [nome, qtd] of Object.entries(pedido.espetos)) {
        if (qtd > 0) ESTOQUE.espetos[nome] -= qtd;
    }

    for (const [nome, qtd] of Object.entries(pedido.refrigerantes)) {
        if (qtd > 0) ESTOQUE.refrigerantes[nome] -= qtd;
    }

    for (const [nome, qtd] of Object.entries(pedido.acai || {})) {
        if (qtd > 0) ESTOQUE.acai[nome] -= qtd;
    }

    for (const [nomeLanche, qtdLanche] of Object.entries(pedido.lanches || {})) {
        if (qtdLanche <= 0) continue;

        const ficha = FICHA_TECNICA[nomeLanche];

        if (!ficha) {
            console.warn(`Ficha técnica não encontrada: ${nomeLanche}`);
            continue;
        }

        for (const [insumo, qtdPorLanche] of Object.entries(ficha)) {
            if (ESTOQUE.insumos[insumo] === undefined) {
                console.warn(`Insumo não cadastrado: ${insumo}`);
                continue;
            }

            ESTOQUE.insumos[insumo] -= qtdPorLanche * qtdLanche;
        }
    }


    // 2. Atualiza Caixa  ✅ AGORA DENTRO DA FUNÇÃO
    CAIXA.total_vendas += pedido.total;
    const metodo = pedido.pagamento.metodo;

    if (metodo === 'Dinheiro') {
        CAIXA.vendas_dinheiro += pedido.total;
        const valorRecebido = pedido.pagamento.valor_recebido;
        const troco = pedido.pagamento.troco;
        const entradaLiquida = valorRecebido - troco;
        CAIXA.saldo_dinheiro += entradaLiquida;

    } else if (metodo === 'Pix') {
        CAIXA.vendas_pix += pedido.total;

    } else if (metodo === 'Cartao') {
        CAIXA.vendas_cartao += pedido.total;
    }
}



function reverterEstoqueECaixa(pedido) {
    // 1. Devolve ao Estoque
    for (const [nome, qtd] of Object.entries(pedido.espetos)) {
        if (qtd > 0) ESTOQUE.espetos[nome] += qtd;
    }
    for (const [nome, qtd] of Object.entries(pedido.refrigerantes)) {
        if (qtd > 0) ESTOQUE.refrigerantes[nome] += qtd;
    }

    // 2. Reverte o Caixa (COM PROTEÇÃO CONTRA VALORES NEGATIVOS)
    CAIXA.total_vendas = Math.max(0, CAIXA.total_vendas - pedido.total);
    const metodo = pedido.pagamento.metodo;
    const valorTotal = pedido.total;

    if (metodo === 'Dinheiro') {
        CAIXA.vendas_dinheiro = Math.max(0, CAIXA.vendas_dinheiro - valorTotal);
        const valorRecebido = pedido.pagamento.valor_recebido;
        const troco = pedido.pagamento.troco;
        const entradaLiquida = valorRecebido - troco;
        CAIXA.saldo_dinheiro -= entradaLiquida;

    } else if (metodo === 'Pix') {
        CAIXA.vendas_pix = Math.max(0, CAIXA.vendas_pix - valorTotal);

    } else if (metodo === 'Cartao') {
        CAIXA.vendas_cartao = Math.max(0, CAIXA.vendas_cartao - valorTotal);
    }
}


