function exportarParaCSV() {
    if (pedidosSalvos.length === 0) {
        alert("Não há pedidos salvos para exportar.");
        return;
    }

    // 1. Define o cabeçalho do CSV (INCLUINDO CMV E LUCRO BRUTO)
    const headers = [
        "ID", "Data", "Cliente", "Endereco", "Status",
        "QtdJantinhas", "TotalVenda", "CustoCMV", "LucroBruto", "MetodoPagamento", "ValorRecebido", "Troco",
        ...Object.keys(PRECOS.espetos).map(e => `Espeto_${e}`),
        ...Object.keys(PRECOS.refrigerantes).map(r => `Refri_${r}`)
    ];

    // 2. Formata os dados de cada pedido
    const rows = pedidosSalvos.map(pedido => {
        // Pega o custo salvo, ou recalcula/assume 0.00 se o pedido for antigo
        const custoCMV = pedido.custo || calcularCusto(pedido);
        const lucroBruto = pedido.total - custoCMV;

        const data = [
            pedido.id,
            `"${pedido.data}"`,
            `"${pedido.cliente_nome}"`,
            `"${pedido.endereco_entrega}"`,
            `"${pedido.status}"`,
            pedido.jantinhas.quantidade,
            pedido.total.toFixed(2),
            custoCMV.toFixed(2),     // NOVO: Custo do Pedido
            lucroBruto.toFixed(2),   // NOVO: Lucro Bruto
            pedido.pagamento.metodo,
            pedido.pagamento.valor_recebido.toFixed(2),
            pedido.pagamento.troco.toFixed(2)
        ];

        // Adiciona a quantidade de cada tipo de espeto e refrigerante
        Object.keys(PRECOS.espetos).forEach(espetoName => {
            data.push(pedido.espetos[espetoName] || 0);
        });

        Object.keys(PRECOS.refrigerantes).forEach(refriName => {
            data.push(pedido.refrigerantes[refriName] || 0);
        });

        return data.join(",");
    });

    // 3. Combina e força o download
    const csvContent = [
        headers.join(","),
        ...rows
    ].join("\n");

    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);

    const link = document.createElement('a');
    link.setAttribute('href', url);

    const now = new Date();
    const filename = `pedidos_${now.getFullYear()}-${(now.getMonth() + 1).toString().padStart(2, '0')}-${now.getDate().toString().padStart(2, '0')}.csv`;

    link.setAttribute('download', filename);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    alert(`Dados exportados com sucesso para o arquivo ${filename}!`);
}

