function updateRealTimeDisplay() {
    // 1. Captura e Cálculo do Total
    const espetos = {};
    const refris = {};
    const lanches = {}; // <-- ADICIONE ESTA LINHA
    const acai = {};
    const acaiAdicionais = {};


    document.querySelectorAll('#espetos-container input[data-name]').forEach(input => espetos[input.dataset.name] = parseInt(input.value) || 0);
    document.querySelectorAll('#refris-container input[data-name]').forEach(input => refris[input.dataset.name] = parseInt(input.value) || 0);
    document.querySelectorAll('#acai-container input[data-name]')
        .forEach(input => acai[input.dataset.name] = parseInt(input.value) || 0);
    document
        .querySelectorAll('#acai-adicionais-container input[data-name]')
        .forEach(input => acaiAdicionais[input.dataset.name] = parseInt(input.value) || 0);

    document.querySelectorAll('#lanches-container input[data-name]').forEach(input => lanches[input.dataset.name] = parseInt(input.value) || 0);

    const qtdJantinhas = parseInt(document.getElementById('qtd-jantinhas').value) || 0;

    // ATUALIZE A CHAMADA DA FUNÇÃO (adicionei "lanches" no final)
    const total = calcularTotal(qtdJantinhas, espetos, refris, lanches, acai, acaiAdicionais);

    const totalDisplay = document.getElementById('total-display');
    if (totalDisplay) totalDisplay.textContent = `Total do Pedido: R$ ${total.toFixed(2)}`;

    // 2. Cálculo do Troco (UX melhorada)
    const metodoPagamento = document.getElementById('metodo-pagamento').value;
    const trocoFeedback = document.getElementById('troco-feedback');
    const valorRecebidoInput = document.getElementById('valor-recebido');

    if (metodoPagamento === 'Dinheiro' && trocoFeedback && valorRecebidoInput) {
        const valorRecebido = parseFloat(valorRecebidoInput.value) || 0.00;
        let trocoADar = 0.0;

        if (valorRecebido >= total) {
            trocoADar = valorRecebido - total;
            trocoFeedback.textContent = `Troco a devolver: R$ ${trocoADar.toFixed(2)}`;
            trocoFeedback.style.color = '#28a745'; // Verde
        } else {
            const faltando = total - valorRecebido;
            trocoFeedback.textContent = `Faltando: R$ ${faltando.toFixed(2)}`;
            trocoFeedback.style.color = '#dc3545'; // Vermelho
        }
    } else if (trocoFeedback) {
        trocoFeedback.textContent = '';
    }
}
function gerarGraficoVendas() {
    const ctx = document.getElementById('graficoVendasHora');
    if (!ctx) return;

    // Criar um array de 24 horas zerado
    const vendasPorHora = new Array(24).fill(0);

    // Percorrer pedidos e extrair a hora
    pedidosSalvos.forEach(p => {
        // Assume que p.data está no formato "DD/MM/AAAA, HH:MM:SS"
        const parteHora = p.data.split(', ')[1];
        if (parteHora) {
            const hora = parseInt(parteHora.split(':')[0]);
            vendasPorHora[hora]++;
        }
    });


    // Filtrar apenas as horas que costumam ter movimento (ex: 17h às 23h) para o gráfico não ficar vazio
    const labels = ["17:00", "18:00", "19:00", "20:00", "21:00", "22:00", "23:00"];
    const dados = [vendasPorHora[17], vendasPorHora[18], vendasPorHora[19], vendasPorHora[20], vendasPorHora[21], vendasPorHora[22], vendasPorHora[23]];

    new Chart(ctx, {
        type: 'bar',
        data: {
            labels: labels,
            datasets: [{
                label: 'Quantidade de Pedidos',
                data: dados,
                backgroundColor: '#007bff',
                borderRadius: 5
            }]
        },
        options: {
            responsive: true,
            scales: {
                y: { beginAtZero: true, ticks: { stepSize: 1 } }
            }
        }
    });
}
