function registrarPerda() {
    const valorSelecionado = document.getElementById('item-perda').value;
    const qtd = parseInt(document.getElementById('qtd-perda').value);

    if (!valorSelecionado || isNaN(qtd) || qtd <= 0) {
        alert("Selecione o item e a quantidade.");
        return;
    }

    let nomeItem = "";
    let custoUnitario = 0;

    // Lógica para identificar o tipo e pegar o custo correto
    if (valorSelecionado === "jantinha") {
        nomeItem = "Jantinha";
        custoUnitario = CUSTOS.jantinha;
    } else if (valorSelecionado.startsWith("espeto:")) {
        nomeItem = valorSelecionado.replace("espeto:", "");
        custoUnitario = CUSTOS.espetos[nomeItem] || 0;
        // Baixa o estoque do espeto
        if (estoque[nomeItem] !== undefined) estoque[nomeItem] -= qtd;
    } else if (valorSelecionado.startsWith("refri:")) {
        nomeItem = valorSelecionado.replace("refri:", "");
        custoUnitario = CUSTOS.refrigerantes[nomeItem] || 0;
        // Baixa o estoque do refrigerante
        if (estoque[nomeItem] !== undefined) estoque[nomeItem] -= qtd;
    }

    const prejuizoTotal = custoUnitario * qtd;

    // Salvar no histórico de perdas
    let perdas = JSON.parse(localStorage.getItem('perdas_jantinha')) || [];
    perdas.push({
        item: nomeItem,
        quantidade: qtd,
        custo: prejuizoTotal,
        data: new Date().toLocaleString()
    });

    localStorage.setItem('perdas_jantinha', JSON.stringify(perdas));
    salvarDados(); // Salva estoque e atualiza tela

    document.getElementById('feedback-perda').innerText =
        `Registrado: ${qtd}x ${nomeItem} (Custo: R$ ${prejuizoTotal.toFixed(2)})`;
    document.getElementById('qtd-perda').value = '';
}
