function calcularResumoHoje() {
    const hoje = new Date();
    hoje.setHours(0, 0, 0, 0);

    const q = query(
        collection(db, "pedidos"),
        where("criadoEm", ">=", hoje)
    );

    onSnapshot(q, (snapshot) => {
        let resumo = {
            total: 0, pix: 0, dinheiro: 0, cartao: 0,
            qtdPedidos: 0, jantinhaEspeto: 0, apenasEspeto: 0,
            totalJantinhas: 0, espetosTotal: 0, lanchesTotal: 0, acaisTotal: 0,
            paes: 0, hamburgueres: 0, queijo: 0, bacon: 0, bananas: 0,
            espetosContagem: {}, refrisContagem: {}
        };

        snapshot.forEach((docSnap) => {
            const p = docSnap.data();
            if (p.status === "Cancelado") return;

            const valor = parseFloat(p.total) || 0;
            resumo.total += valor;
            resumo.qtdPedidos++;

            // 1. Métodos de Pagamento (Usando p.pagamento.metodo que é o seu padrão)
            const met = (p.pagamento?.metodo || "").toLowerCase();
            if (met.includes("pix")) resumo.pix += valor;
            else if (met.includes("dinheiro")) resumo.dinheiro += valor;
            else if (met.includes("cartao") || met.includes("cartão") || met.includes("debito") || met.includes("credito")) {
                resumo.cartao += valor;
            }

            // 2. Jantinhas e Espetos
            const qtdJantinha = parseInt(p.jantinhas?.quantidade || 0);
            const temEspeto = Object.values(p.espetos || {}).some(q => q > 0);

            if (qtdJantinha > 0) resumo.totalJantinhas += qtdJantinha;
            if (qtdJantinha > 0 && temEspeto) resumo.jantinhaEspeto++;
            else if (temEspeto && qtdJantinha === 0) resumo.apenasEspeto++;

            // 3. Lanches e Insumos
            if (p.lanches) {
                Object.entries(p.lanches).forEach(([nome, qtd]) => {
                    if (qtd > 0) {
                        resumo.lanchesTotal += qtd;
                        resumo.paes += qtd;
                        resumo.hamburgueres += qtd;
                        resumo.queijo += qtd;
                        if (nome.toLowerCase().includes("bacon")) resumo.bacon += qtd;
                    }
                });
            }

            // 4. Açaí e Bananas
            if (p.acai) {
                Object.values(p.acai).forEach(qtd => {
                    if (qtd > 0) {
                        resumo.acaisTotal += qtd;
                        resumo.bananas += qtd;
                    }
                });
            }

            // 5. Rankings
            Object.entries(p.espetos || {}).forEach(([nome, qtd]) => {
                if (qtd > 0) {
                    resumo.espetosTotal += qtd;
                    resumo.espetosContagem[nome] = (resumo.espetosContagem[nome] || 0) + qtd;
                }
            });
            Object.entries(p.refrigerantes || p.bebidas || {}).forEach(([nome, qtd]) => {
                if (qtd > 0) resumo.refrisContagem[nome] = (resumo.refrisContagem[nome] || 0) + qtd;
            });
        });

        // 6. Identificar Mais Vendidos
        const espetoMaisVendido = Object.keys(resumo.espetosContagem).length > 0
            ? Object.keys(resumo.espetosContagem).reduce((a, b) => resumo.espetosContagem[a] > resumo.espetosContagem[b] ? a : b)
            : "N/A";

        const refriMaisVendido = Object.keys(resumo.refrisContagem).length > 0
            ? Object.keys(resumo.refrisContagem).reduce((a, b) => resumo.refrisContagem[a] > resumo.refrisContagem[b] ? a : b)
            : "N/A";

        // 7. Atualização da Tela
        const setUI = (id, val) => { if (document.getElementById(id)) document.getElementById(id).innerText = val; };

        setUI("resumo-total", `R$ ${resumo.total.toFixed(2)}`);
        setUI("resumo-pix", `R$ ${resumo.pix.toFixed(2)}`);
        setUI("resumo-dinheiro", `R$ ${resumo.dinheiro.toFixed(2)}`);
        setUI("resumo-cartao", `R$ ${resumo.cartao.toFixed(2)}`);
        setUI("resumo-pedidos", resumo.qtdPedidos);
        setUI("resumo-jantinhas", resumo.totalJantinhas);
        setUI("resumo-espetos", resumo.espetosTotal);
        setUI("resumo-lanches", resumo.lanchesTotal);
        setUI("resumo-acais", resumo.acaisTotal);

        // Insumos
        setUI("consumo-paes", resumo.paes);
        setUI("consumo-hamburguer", resumo.hamburgueres);
        setUI("consumo-queijo", resumo.queijo);
        setUI("consumo-bacon", resumo.bacon);
        setUI("consumo-bananas", resumo.bananas);

        // Mais Vendidos
        setUI("espeto-mais-vendido", espetoMaisVendido);
        setUI("refri-mais-vendido", refriMaisVendido);
    }); // Fim do onSnapshot
} // Fim da Função
