function calcularConsumoPorFichaTecnica() {
    const hojeISO = new Date().toISOString().slice(0, 10);

    let consumo = {
        acai: 0,
        lanches: 0,
        espetos: 0,
        jantinhas: 0,

        pao: 0,
        hamburguer: 0,
        queijo: 0,
        bacon: 0,
        abacaxi: 0,
        banana: 0
    };

    pedidosSalvos.forEach(p => {
        if (!p.data || p.status !== 'Concluído') return;
        if (!p.data.startsWith(hojeISO)) return;

        // Jantinhas
        consumo.jantinhas += p.jantinhas?.quantidade || 0;

        // Espetos
        Object.values(p.espetos || {}).forEach(q => consumo.espetos += q);

        // 🍔 Lanches
        Object.entries(p.lanches || {}).forEach(([nome, qtd]) => {
            consumo.lanches += qtd;

            const ficha = FICHA_TECNICA[nome];
            if (!ficha) return;

            Object.entries(ficha).forEach(([insumo, qtdInsumo]) => {
                consumo[insumo] =
                    (consumo[insumo] || 0) + (qtd * qtdInsumo);
            });
        });

        // 🍧 Açaí
        Object.values(p.acai || {}).forEach(qtd => {
            consumo.acai += qtd;
            consumo.banana += qtd;
        });
    });

    return consumo;
}

function atualizarConsumoDiario() {
    const c = calcularConsumoPorFichaTecnica();

    Object.entries(c).forEach(([k, v]) => {
        const el = document.getElementById(`c-${k}`);
        if (el) el.textContent = v;
    });
}
function baixarInsumosLanche(nomeLanche, quantidade = 1) {
    const ficha = FICHA_TECNICA_LANCHES[nomeLanche];
    if (!ficha) {
        console.warn(`Ficha técnica não encontrada: ${nomeLanche}`);
        return false;
    }

    // 🔴 Valida estoque antes
    for (const insumo in ficha) {
        const necessario = ficha[insumo] * quantidade;
        if ((ESTOQUE.insumos[insumo] || 0) < necessario) {
            alert(`❌ Estoque insuficiente de ${insumo}`);
            return false;
        }
    }

    // ✅ Dá baixa
    for (const insumo in ficha) {
        ESTOQUE.insumos[insumo] -= ficha[insumo] * quantidade;
    }

    salvarEstoque();
    return true;
}
