async function fecharCaixa() {
    // 1. DEFINA SUA SENHA AQUI
    const SENHA_MESTRE = "121325";

    // 2. PRIMEIRA TRAVA: Confirmação visual
    const confirmacao = confirm("⚠️ Você deseja REALMENTE encerrar o caixa agora?\nEsta ação não pode ser desfeita.");
    if (!confirmacao) return;

    // 3. SEGUNDA TRAVA: Solicitação de Senha
    const senhaDigitada = prompt("Digite a senha de gerente para autorizar o fechamento:");

    if (senhaDigitada === null) return; // Se o usuário clicar em "Cancelar" no prompt

    if (senhaDigitada !== SENHA_MESTRE) {
        alert("❌ Senha incorreta! O fechamento foi abortado.");
        return;
    }
    const hoje = new Date();
    hoje.setHours(0, 0, 0, 0);
    const amanha = new Date(hoje);
    amanha.setDate(amanha.getDate() + 1);

    try {
        const q = query(
            collection(db, "pedidos"),
            where("timestamp", ">=", hoje.getTime()),
            where("timestamp", "<", amanha.getTime())
        );

        const querySnapshot = await getDocs(q);

        if (querySnapshot.empty) {
            alert("Nenhum pedido encontrado para fechar hoje.");
            return;
        }

        let resumo = {
            data: hoje.toLocaleDateString(),
            pedidos: 0, faturamento: 0, custo: 0, taxaCartao: 0,
            lucroBruto: 0, lucroLiquido: 0,
            pagamentos: { pix: 0, dinheiro: 0, cartao: 0 },
            lucroPorCategoria: { jantinhas: 0, espetos: 0, lanches: 0, refrigerantes: 0, acai: 0 }
        };

        // 1. Processamento dos cálculos
        querySnapshot.forEach((doc) => {
            const p = doc.data();
            resumo.pedidos++;
            resumo.faturamento += p.total || 0;

            if (p.jantinhas?.quantidade > 0) {
                const custoJ = p.jantinhas.quantidade * (CUSTOS.jantinha || 0);
                resumo.custo += custoJ;
                resumo.lucroPorCategoria.jantinhas += custoJ;
            }

            const categorias = ['espetos', 'lanches', 'refrigerantes', 'acai', 'acaiAdicionais'];
            categorias.forEach(cat => {
                if (p[cat]) {
                    Object.entries(p[cat]).forEach(([nome, qtd]) => {
                        if (qtd > 0 && CUSTOS[cat]?.[nome] !== undefined) {
                            const custoItem = CUSTOS[cat][nome] * qtd;
                            resumo.custo += custoItem;
                            const catKey = cat === 'acaiAdicionais' ? 'acai' : cat;
                            resumo.lucroPorCategoria[catKey] = (resumo.lucroPorCategoria[catKey] || 0) + custoItem;
                        }
                    });
                }
            });

            const m = (p.pagamento?.metodo || '').toLowerCase();
            if (m.includes('dinheiro')) resumo.pagamentos.dinheiro += p.total;
            else if (m.includes('pix')) resumo.pagamentos.pix += p.total;
            else {
                resumo.pagamentos.cartao += p.total;
                resumo.taxaCartao += (p.total * 0.025);
            }
        });

        resumo.lucroBruto = resumo.faturamento - resumo.custo;
        resumo.lucroLiquido = resumo.lucroBruto - resumo.taxaCartao;

        // 2. AÇÕES DE FECHAMENTO (Onde a mágica acontece)

        // A) Gera o PDF
        gerarPDFfechamento(resumo);

        // B) Salva no Histórico (Firebase)
        await salvarFechamentoNoFirebase(resumo);

        // C) DELETA OS PEDIDOS DO BANCO (Para zerar a tela de verdade)
        // Usamos batch para deletar todos de uma vez e economizar recursos
        const batch = writeBatch(db);
        querySnapshot.forEach((documento) => {
            batch.delete(documento.ref);
        });
        await batch.commit();

        // D) Limpa o que sobrou na interface local
        resetarInterfacePosFechamento();

        alert(`✅ CAIXA ZERADO!\nO histórico foi salvo e os pedidos foram removidos do painel.`);

    } catch (error) {
        console.error("Erro no fechamento:", error);
        alert("Erro técnico: Verifique se 'writeBatch' foi importado no topo do script.");
    }
}

