function calcularConsumoMedio(item, categoria, dias = 7) {
    let total = 0;
    let count = 0;

    for (let i = 0; i < dias; i++) {
        const d = new Date();
        d.setDate(d.getDate() - i);
        const iso = d.toISOString().slice(0, 10);
        const fechamento = JSON.parse(localStorage.getItem(`fechamento_estoque_${iso}`));

        if (!fechamento) continue;

        const ini = fechamento.estoqueInicial?.[categoria]?.[item];
        const fim = fechamento.estoqueFinal?.[categoria]?.[item];

        if (ini !== undefined && fim !== undefined) {
            total += (ini - fim);
            count++;
        }
    }

    return count > 0 ? total / count : 0;
}
function gerarListaCompraDiaria() {
    const lista = [];

    for (const categoria in ESTOQUE) {
        for (const item in ESTOQUE[categoria]) {
            const estoqueAtual = ESTOQUE[categoria][item];

            // 🔥 REGRA CRÍTICA: estoque baixo
            if (estoqueAtual <= ESTOQUE_MINIMO_COMPRA) {
                lista.push({
                    categoria,
                    item,
                    estoqueAtual,
                    consumoMedio: "BAIXO ESTOQUE",
                    comprar: Math.max(
                        ESTOQUE_MINIMO_COMPRA * 2 - estoqueAtual,
                        5
                    ),
                    prioridade: estoqueAtual === 0 ? "❌ CRÍTICO" : "🔥 URGENTE"
                });
                continue;
            }

            // REGRA NORMAL
            const consumoMedio = calcularConsumoMedio(item, categoria);
            const ideal = Math.ceil(consumoMedio * COBERTURA_DIAS);
            const comprar = ideal - estoqueAtual;

            if (comprar > 0) {
                lista.push({
                    categoria,
                    item,
                    estoqueAtual,
                    consumoMedio: consumoMedio.toFixed(1),
                    comprar,
                    prioridade: "🟡 NORMAL"
                });
            }
        }
    }

    return lista;
}


