async function limparPainelGeral() {
    if (!confirm("Deseja limpar todos os pedidos pendentes e prontos do Painel?")) return;

    try {
        // Importa as ferramentas necessárias caso não estejam no topo do arquivo
        const { collection, query, where, getDocs, doc, updateDoc } = await import("https://www.gstatic.com/firebasejs/10.7.1/firebase-firestore.js");

        // window.db deve ser a sua instância do Firestore inicializada
        const q = query(
            collection(window.db, "pedidos"),
            where("status", "in", ["Pendente", "Em preparo", "Preparando", "Pronto"])
        );

        const querySnapshot = await getDocs(q);

        if (querySnapshot.empty) {
            alert("O painel já está limpo!");
            return;
        }

        const promessas = querySnapshot.docs.map(docSnap => {
            const docRef = doc(window.db, "pedidos", docSnap.id);
            return updateDoc(docRef, { status: "Finalizado" });
        });

        await Promise.all(promessas);
        alert(`Sucesso! ${querySnapshot.size} pedidos removidos.`);

    } catch (error) {
        console.error("Erro ao limpar painel:", error);
    }
}

