function exibirPedidoNaTela(pedido, id) {
    const container = document.getElementById('container-pedidos'); // Verifique se este ID existe
    if (!container) {
        console.error("ERRO: Elemento 'container-pedidos' não encontrado no HTML!");
        return;
    }

    // Cria o card do pedido
    const card = document.createElement('div');
    card.id = `pedido-${id}`;
    card.className = `card-pedido ${pedido.status}`;
    card.innerHTML = `
        <h3>${pedido.cliente} - R$ ${pedido.total.toFixed(2)}</h3>
        <p>Status: <strong>${pedido.status}</strong></p>
        <div class="itens">
            ${pedido.itens.map(i => `<div>${i.qtd}x ${i.nome}</div>`).join('')}
        </div>
    `;
    container.prepend(card);
}
